import React from 'react'
import { useState } from 'react'
import emailjs from '@emailjs/browser'
import { useForm } from 'react-hook-form'

const Contato = () => {
    const [successMessage, setSuccessMessage] = useState("");
    const { register, handleSubmit, formState: { errors } } = useForm();

    const serviceID = 'service_jdbw8zz';
    const templateID = 'template_e4udane';
    const userID = 'user_SSLfmmXgVv6jLC0TuXdAg';

    const onSubmit = (data, r) => {
        sendEmail(
            serviceID,
            templateID,
            {
                nome: data.nome,
                telefone: data.telefone,
                email: data.email,
                assunto: data.assunto,
                mensagem: data.mensagem
            },
            userID
        )
        r.target.reset();
    }

    const sendEmail = (serviceID, templateID, variables, userID) => {
        emailjs.send(serviceID, templateID, variables, userID)
            .then(() => {
                setSuccessMessage('Mensagem enviada com sucesso! Entraremos em contato assim que possível.');
            }).catch(err => console.error(`Algo deu errado ${err}`));
    };

    return (
        <div className='contato'>
            <div className='text-center'>
                <h1>Contato</h1>
                <p>Para orçamentos, reservas, agendamentos, alterações de reserva e informações.</p>
                <span className="success-message">{successMessage}</span>
            </div>
            <div className='container'>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className='row'>
                        <div className='col-md-6 col-xs-12 '>
                            {/* NOME INPUT */}
                            <div className='text-center'>
                                <input
                                    type='text'
                                    className='form-control'
                                    placeholder='Nome'
                                    name='nome'
                                    aria-invalid={errors.nome ? "true" : "false"}
                                    {...register("nome", {
                                        required: "Por favor, digite seu nome",
                                        maxLength: {
                                            value: 20,
                                            message:
                                                "Please enter a name with fewer than 20 characters",
                                        },
                                    })}
                                />
                                <div className='line'></div>
                            </div>
                            <span className='error-message'>
                                {errors.nome && errors.nome.message}
                            </span>
                            {/* TELEFONE INPUT */}
                            <div className='text-center'>
                                <input
                                    type='text'
                                    className='form-control'
                                    placeholder='Telefone'
                                    phone='tefefone'
                                    aria-invalid={errors.nome ? "true" : "false"}
                                    {...register('telefone',{
                                            required: 'Por favor, insira o número do seu telefone',
                                        })
                                    }
                                />
                                <div className='line'></div>
                            </div>
                            <span className="error-message">
                                {errors.telefone && errors.telefone.message}
                            </span>
                            {/* EMAIL INPUT */}
                            <div className='text-center'>
                                <input
                                    type='email'
                                    className='form-control'
                                    placeholder='E-mail'
                                    name='email'
                                    {...register('email', {
                                            required: "Por favor, forneça seu email!",
                                            pattern: {
                                                value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                                                message: "Email inválido"
                                            }
                                        })
                                    }
                                />
                                <div className='line'></div>
                            </div>
                            <span className="error-message">
                                {errors.email && errors.email.message}
                            </span>
                            {/* ASSUNTO INPUT */}
                            <div className='text-center'>
                                <input
                                    type='text'
                                    className='form-control'
                                    placeholder='Assunto'
                                    name='assunto'
                                    {...register('assunto', {
                                            required: 'OOPS, você esqueceu de adicionar um assunto',
                                        })
                                    }
                                />
                                <div className='line'></div>
                            </div>
                            <span className="error-message">
                                {errors.assunto && errors.assunto.message}
                            </span>
                        </div>
                        <div className='col-md-6 col-sm-12'>
                            {/* MENSAGEM */}
                            <div className='text-center'>
                                <textarea
                                    type='text'
                                    className='form-control'
                                    placeholder='Digite aqui a sua mensagem'
                                    name='mensagem'
                                    {...register('mensagem',{
                                            required: "Por favor, digite aqui a sua mensagem...",
                                        })
                                    }
                                ></textarea>
                                <div className='line'></div>
                            </div>
                            <span className="error-message">
                                {errors.mensagem && errors.mensagem.message}
                            </span>
                            <button className='btn-contato contact-btn' type='submit'>ENVIAR</button>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default Contato
