import { useEffect } from 'react'
import { useLocation } from 'react-router-dom'

export default function ScrollTop ({}) {
    const { inicio } = useLocation();
    useEffect (() => {
        window.scrollTo(0, 0);
    }, [inicio]);

    return null;
}
