import React, { useState } from 'react'
import emailjs from '@emailjs/browser'
import { useForm } from 'react-hook-form'

const Contato = () => {
    const [successMessage, setSuccessMessage] = useState('');
    const { register, handleSubmit, errors } = useForm();

    const serviceID = 'service_jdbw8zz';
    const templateID = 'template_e4udane';
    const userID = 'user_SSLfmmXgVv6jLC0TuXdAg';

    const onSubmit = (data, r) => {
        sendEmail(
            serviceID,
            templateID,
            {
                nome: data.nome,
                telefone: data.telefone,
                email: data.email,
                assunto: data.assunto,
                mensagem: data.mensagem
            },
            userID
        )
        r.target.reset();
    }

    const sendEmail = (serviceID, templateID, variables, userID) => {
        emailjs.send(serviceID, templateID, variables, userID)
            .then(() => {
                setSuccessMessage('Mensagem enviada com sucesso! Entraremos em contato assim que possível.');
            }).catch(err => console.error(`Algo deu errado ${err}`));
    };

    return (
        <div className='contato'>
            <div className='text-center'>
                <h1>Contato</h1>
                <p>Para orçamentos, reservas, agendamentos, alterações de reserva e informações.</p>
                <span className="success-message">{successMessage}</span>
            </div>
            <div className='container'>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className='row'>
                        <div className='col-md-6 col-xs-12 '>
                            {/* NOME INPUT */}
                            <div className='text-center'>
                                <input
                                    type='text'
                                    className='form-control'
                                    placeholder='Nome'
                                    name='nome'
                                    ref={
                                        register({
                                            required: 'Por favor, insira seu nome',
                                            maxLength: {
                                                value: 20,
                                                message: 'Digite um nome com menos de 20 caracteres!'
                                            }
                                        })
                                    }
                                />
                                <div className='line'></div>
                            </div>
                            <span className='error-message'>
                                {errors.nome && errors.nome.message}
                            </span>
                            {/* TELEFONE INPUT */}
                            <div className='text-center'>
                                <input
                                 type='text' className='form-control' placeholder='Telefone' phone='tefefone' />
                                <div className='line'></div>
                            </div>
                            {/* EMAIL INPUT */}
                            <div className='text-center'>
                                <input type='email' className='form-control' placeholder='E-mail' name='email' />
                                <div className='line'></div>
                            </div>
                            {/* ASSUNTO INPUT */}
                            <div className='text-center'>
                                <input type='text' className='form-control' placeholder='Assunto' name='assunto' />
                                <div className='line'></div>
                            </div>
                        </div>
                        <div className='col-md-6 col-sm-12'>
                            {/* MENSAGEM */}
                            <div className='text-center'>
                                <textarea type='text' className='form-control' placeholder='Digite aqui a sua mensagem' name='mensagem'></textarea>
                                <div className='line'></div>
                            </div>
                            <button type="submit" value='Send' className='btn-contato contact-btn'>ENVIAR</button>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default Contato
